package chap09;

public class MathExample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("2승의 3승은"+Math.pow(2, 3));
		System.out.println("16의 제곱근은?"+Math.sqrt(16));
		System.out.println("4와7중 큰수는?"+Math.max(4, 7));
		System.out.println("4와7중작은수는?"+Math.min(4, 7));

	}

}
